CREATE DATABASE inhacinema;
USE inhacinema;
CREATE TABLE MEMBERS(memberIndex INT(10) NOT NULL AUTO_INCREMENT PRIMARY KEY, memberId VARCHAR(12) NOT NULL, memberPw VARCHAR(12) NOT NULL, memberName VARCHAR(10) NOT NULL, memberBirth DATE, memberEmail VARCHAR(100), registerDate TIMESTAMP, memberGrade VARCHAR(10));
CREATE TABLE MOVIE(movieIndex INT(10) NOT NULL AUTO_INCREMENT PRIMARY KEY, movieTitle VARCHAR(100) NOT NULL, movieDirector VARCHAR(100), movieGenre VARCHAR(100), movieLength VARCHAR(10), movieGrade VARCHAR(10), movieOpenDate DATE, movieCast VARCHAR(100), movieImage VARCHAR(100), movieAudience INT(10));
CREATE TABLE REVIEW(reviewIndex INT(10) NOT NULL AUTO_INCREMENT PRIMARY KEY, movieIndex INT(10) NOT NULL, memberIndex INT(10) NOT NULL, reviewContent VARCHAR(200), reviewDate TIMESTAMP);
CREATE TABLE SCHEDULES(scheduleIndex INT(10) NOT NULL AUTO_INCREMENT PRIMARY KEY, movieIndex INT(10) NOT NULL, cinemaIndex VARCHAR(10) NOT NULL, screenDate VARCHAR(10), startTime VARCHAR(10), seatState VARCHAR(1000));
CREATE TABLE TICKET(ticketIndex INT(10) NOT NULL AUTO_INCREMENT PRIMARY KEY, scheduleIndex INT(10) NOT NULL, memberIndex INT(10) NOT NULL, seatNum VARCHAR(3), ticketDate TIMESTAMP, ticketPrice VARCHAR(10));



